from PySide6.QtWidgets import QDoubleSpinBox, QAbstractSpinBox
from .basic import QtAdapter


class AdjustmentAdapter(QtAdapter):
    """Adapter for QDoubleSpinBox (and similar) widgets."""
    widget_types = ["spin_button", "adjustment"]
    _check_widget_type = QAbstractSpinBox

    _wid_read = lambda c, w: w.value()
    _wid_write = lambda c, w, v: w.setValue(float(v) if v is not None else 0.0)
    _signal = "valueChanged"

    pass  # end of class
