from PySide6.QtWidgets import QLabel
from .basic import QtAdapter


class ArrowAdapter(QtAdapter):
    """
    Adapter for arrow direction indicators.
    Displays a unicode arrow character in a QLabel.
    Equivalent of the GTK Arrow widget.
    """
    widget_types = ["arrow"]
    _check_widget_type = QLabel

    _ARROWS = {
        "up": "▲", "down": "▼", "left": "◄", "right": "►", None: ""
    }

    _wid_read = lambda c, w: w.property("arrow_direction")
    _wid_write = lambda c, w, v: (
        w.setText(ArrowAdapter._ARROWS.get(v, "")),
        w.setProperty("arrow_direction", v)
    )
    _signal = None  # display-only

    pass  # end of class
