from PySide6.QtGui import QAction
from .basic import QtAdapter


class CheckMenuItemAdapter(QtAdapter):
    """Adapter for checkable QAction menu items."""
    widget_types = ["check_menu_item"]
    _check_widget_type = QAction

    _wid_read = lambda c, w: w.isChecked()
    _wid_write = lambda c, w, v: w.setChecked(bool(v))
    _signal = "toggled"

    def _connect_widget(self):
        self._widget.setCheckable(True)
        super()._connect_widget()

    pass  # end of class
