from .widgets.color_button import QtColorButton
from ._qt_color_utils import parse_color, color_to_string
from .basic import QtAdapter


class ColorButtonAdapter(QtAdapter):
    """Adapter for QtColorButton compound widgets."""
    widget_types = ["color_button", "color"]
    _check_widget_type = QtColorButton

    _wid_read = lambda c, w: w.get_color()
    _wid_write = lambda c, w, v: w.set_color(color_to_string(parse_color(v)) if v is not None else "#000000")
    _signal = "colorChanged"

    pass  # end of class
