from PySide6.QtWidgets import QLineEdit
from .basic import QtAdapter


class EntryAdapter(QtAdapter):
    """Adapter for QLineEdit widgets."""
    widget_types = ["entry", "input"]
    _check_widget_type = QLineEdit

    _wid_read = lambda c, w: w.text()
    _wid_write = lambda c, w, v: w.setText(str(v) if v is not None else "")
    _signal = "textChanged"

    pass  # end of class
