from PySide6.QtWidgets import QGroupBox
from .basic import QtAdapter


class ExpanderAdapter(QtAdapter):
    """
    Adapter for collapsible QGroupBox widgets (setCheckable=True).
    Equivalent of the GTK Expander widget.
    """
    widget_types = ["expander"]
    _check_widget_type = QGroupBox

    _wid_read = lambda c, w: w.isChecked()
    _wid_write = lambda c, w, v: w.setChecked(bool(v))
    _signal = "toggled"

    def _connect_widget(self):
        self._widget.setCheckable(True)
        super()._connect_widget()

    pass  # end of class
