from .widgets.file_chooser_button import QtFileChooserButton
from .basic import QtAdapter


class FileChooserAdapter(QtAdapter):
    """Adapter for QtFileChooserButton compound widgets."""
    widget_types = ["file_chooser"]
    _check_widget_type = QtFileChooserButton

    _wid_read = lambda c, w: w.get_filename()
    _wid_write = lambda c, w, v: w.set_filename(v)
    _signal = "fileChanged"

    pass  # end of class
