from PySide6.QtWidgets import QLabel
from .basic import QtAdapter


class LabelAdapter(QtAdapter):
    """Adapter for QLabel widgets (display-only, no signal)."""
    widget_types = ["label"]
    _check_widget_type = QLabel

    _wid_read = lambda c, w: w.text()
    _wid_write = lambda c, w, v: w.setText(str(v) if v is not None else "")
    _signal = None  # labels are read-only display

    pass  # end of class
