from PySide6.QtWidgets import QLabel
from .basic import QtAdapter


class LinkButtonAdapter(QtAdapter):
    """
    Adapter for hyperlink QLabel widgets (openExternalLinks=True).
    Equivalent of the GTK LinkButton widget.
    """
    widget_types = ["link_button"]
    _check_widget_type = QLabel

    _wid_read = lambda c, w: w.text()
    _wid_write = lambda c, w, v: w.setText(str(v) if v is not None else "")
    _signal = None  # link buttons are display-only

    def _connect_widget(self):
        self._widget.setOpenExternalLinks(True)
        super()._connect_widget()

    pass  # end of class
