from .widgets.scale_entry import QtScaleEntry
from .basic import QtAdapter


class ScaleEntryAdapter(QtAdapter):
    """Adapter for QtScaleEntry compound widgets."""
    widget_types = ["scale_entry"]
    _check_widget_type = QtScaleEntry

    _wid_read = lambda c, w: w.get_value()
    _wid_write = lambda c, w, v: w.set_value(v)
    _signal = "changed"

    pass  # end of class
