from PySide6.QtWidgets import QTextEdit
from .basic import QtAdapter


class TextViewAdapter(QtAdapter):
    """Adapter for QTextEdit widgets."""
    widget_types = ["text_view"]
    _check_widget_type = QTextEdit

    _wid_read = lambda c, w: w.toPlainText()
    _wid_write = lambda c, w, v: w.setPlainText(str(v) if v is not None else "")
    _signal = "textChanged"

    pass  # end of class
