from PySide6.QtWidgets import QCheckBox, QAbstractButton
from .basic import QtAdapter


class ToggleButtonAdapter(QtAdapter):
    """Adapter for QCheckBox / checkable QPushButton widgets."""
    widget_types = ["toggle_button", "check_button"]
    _check_widget_type = QAbstractButton

    _wid_read = lambda c, w: w.isChecked()
    _wid_write = lambda c, w, v: w.setChecked(bool(v))
    _signal = "toggled"

    pass  # end of class
