import os as _os
_resource_filename = lambda path: _os.path.normpath(
    _os.path.join(_os.path.dirname(_os.path.abspath(__file__)), path))
resource_filename = lambda _n, path: _resource_filename(path)
from PySide6.QtGui import QIcon, QPixmap


def get_icon_list():
    """
    Returns a QIcon populated with all available PyXRD icon sizes.
    Replaces the GTK list-of-GdkPixbufs approach.
    """
    icon = QIcon()
    for size in (16, 24, 32, 48, 64, 128):
        path = resource_filename(__name__, "icons/pyxrd_icon_%dx%d.png" % (size, size))
        pm = QPixmap(path)
        if not pm.isNull():
            icon.addPixmap(pm)
    return icon
