import os as _os
_resource_filename = lambda path: _os.path.normpath(
    _os.path.join(_os.path.dirname(_os.path.abspath(__file__)), path))
resource_filename = lambda _n, path: _resource_filename(path)

from pyxrd.generic.views import BaseView, DialogView

class InlineGoniometerView(BaseView):
    """
        The inline Goniometer view.
    """

    builder = resource_filename(__name__, "ui/goniometer.ui")
    top = "edit_goniometer"

    widget_format = "gonio_%s"

    @property
    def import_combo_box(self):
        return self["cmb_import_gonio"]

    pass # end of class


class WavelengthDistributionView(DialogView):
    """
        The wavelength distribution view.
    """   
    subview_builder = resource_filename(__name__, "ui/wavelength_distribution.ui")
    subview_toplevel = "edit_wld"

    widget_format = "wld_%s"
    
    pass # end of class